(182, 23, 99, 0, 'scriptAdded', 10, '<script>\r\nvar acc = document.getElementsByClassName(\"accordion\");\r\nvar i;\r\n\r\nfor (i = 0; i < acc.length; i++) {\r\n  acc[i].addEventListener(\"click\", function() {\r\n    this.classList.toggle(\"active\");\r\n    var panel = this.nextElementSibling;\r\n    if (panel.style.display === \"block\") {\r\n      panel.style.display = \"none\";\r\n    } else {\r\n      panel.style.display = \"block\";\r\n    }\r\n  });\r\n}\r\n</script>', '2021-10-13 08:09:13', '2021-10-13 08:09:13', 1),
(183, 23, 100, 0, 'footText', 1, 'Production-Grade Kubernetes, also known as <b>K8s</b>, is an open-source system for automating deployment, scaling, and management of containerized applications.\r\n<br><br>\r\nSettings allow for <b>orchestration</b> of these containers - I.e. Assigning various parameters such as ~Numbers of containers~ , automating ~scaling and resources~.', '2021-10-13 08:09:23', '2021-10-13 08:09:23', 1);
